import React, { useState } from 'react';
import './App.css';
import Preview from './components/Preview';
import Message from './components/Message';
import NotesContainer from './components/Notes/NotesContainer';
import NotesList from './components/Notes/NotesList';
import Note from './components/Notes/Note';

function App() {

  const[notes, setNotes] = useState([]);
  const[title, setTitle] = useState(''); 
  const[content, setContent] = useState(''); 
  const[selectedNote, setSelectedNote] = useState(null); 
  const[creating, setCreating] = useState(false); 
  const[editing, setEditing] = useState(false);   

  const changeTitleHandler = (event) => {
    setTitle(event.target.value);
  }

  const changeContentHandler = (event) => {
    setContent(event.target.value);
  }

  const saveNoteHandler = () => {
    const note = {
      id: new Date(),
      title: title,
      content: content
    }

    const updatedNote = [...notes, note];

    setNotes(updatedNote);
    setCreating(false);
    setSelectedNote(note.id);
    setTitle('');
    setContent('');
  }

  const selectedNoteHandler = noteId => {
    setSelectedNote(noteId);
    setCreating(false);
    setEditing(false);
  }

  const editNoteHandler = () => {
    const note = notes.find(note => note.id === selectedNote);
    setEditing(true);
    setTitle(note.title);
    setContent(note.content);
  }

  const updateNoteHandler = () => {
    const updatedNotes = [...notes];
    const noteIndex = notes.findIndex(note => note.id === selectedNote);
    updatedNotes[noteIndex] = {
      id: selectedNote,
      title: title,
      content: content
    };

    setNotes(updatedNotes);
    setEditing(false);
    setTitle('');
    setContent('');
  }

  const addNoteHandler = () => {
    setCreating(true);
    setEditing(false);
    setTitle('');
    setContent('');
  }

  const getAddNote = () => {
    return (
      <div>
        <h2>إضافة ملاحظة جديدة</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="العنوان"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="النص"
            value={content}
            onChange={changeContentHandler}
          />

          <a href="#" className="button green" onClick={saveNoteHandler}>
            حفظ
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {
    if (notes.length === 0) {
      return <Message title="لا يوجد ملاحظة" />
    }

    if (!selectedNote) {
      return <Message title="الرجاء اختيار ملاحظة" />
    }

    const note = notes.find(note => {
      return note.id ===selectedNote;
    });

    let noteDisplay = (
      <div>
        <h2>{note.title}</h2>
        <p>{note.content}</p>
      </div>
    )

    if (editing) {
      noteDisplay = (
        <div>
          <h2>تعديل ملاحظة</h2>
          <form>
            <input
              type="text"
              name="title"
              className="form-input mb-30"
              placeholder="العنوان"
              value={title}
              onChange={changeTitleHandler}
            />

            <textarea
              rows="10"
              name="content"
              className="form-input"
              placeholder="النص"
              value={content}
              onChange={changeContentHandler}
            />

            <a href="#" className="button green" onClick={updateNoteHandler}>
              تعديل
            </a>

          </form>
        </div>
      );
    }

    return (
      <div>
        {!editing &&
                    <div className="note-operations">
                        <a href="#" onClick={editNoteHandler}> 
                            <i className="fa fa-pencil-alt" />
                        </a>
                        <a href="#" >  
                            <i className="fa fa-trash" />
                        </a>
                    </div>
                }

                {noteDisplay}
            </div>
    );
  };

  return (
    <div className="App">
      <NotesContainer>
        <NotesList>
          {notes.map(note => <Note 
          key={note.id} 
          title={note.title}
          noteClicked={() => selectedNoteHandler(note.id)}
          active={selectedNote === note.id} />)}
        </NotesList>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
        </NotesContainer>
      <Preview>
        {creating ? getAddNote() : getPreview()}
      </Preview>
    </div>
  );
}

export default App;
